import KeyboardEvents from '@/components/KeyboardEvents.vue';
import config from '@/config.js';
import validation from '@/js/status/validation.js';
import ElIncrementSearch from '@/components/IncrementSearch.vue';
import time from '@/js/status/time.js';
export default {
  name: 'nyukinNyuryoku',
  components: {
    KeyboardEvents: KeyboardEvents,
    ElIncrementSearch: ElIncrementSearch
  },
  data: function data() {
    return {
      data: {
        syoriKubun: 0,
        nyukinDate: "",
        search: {
          nyukinDate: "",
          syouhinCode: "",
          syouhinName: "",
          bikou: "",
          result: [],
          selectedIndex: null
        },
        //更新ボタンの文字
        updateButton: "更新",
        disabled: {
          nyukinConfirm: false
        },
        shijoData: [],
        dialogVisible: false,
        receptionNumber: "",
        nyukinModel: {
          meisai: [],
          uriage: [],
          nyukinDate: "",
          shijoCode: "",
          shijoName: "",
          bikou: ""
        }
      }
    };
  },
  methods: {
    keyboardEvent: function keyboardEvent(e) {
      if (e.keyCode == config.KEY_CODES.F4) {
        if (!this.data.disabled.nyukinConfirm && this.data.syoriKubun != 0) {
          this.data.dialogVisible = true;
        }
      }

      if (e.keyCode == config.KEY_CODES.F9) {
        this.update();
      }

      if (e.keyCode == config.KEY_CODES.F10) {
        this.init();
      }

      if (e.keyCode == config.KEY_CODES.F12) {
        this.finish();
      }
    },
    createMeisaiData: function createMeisaiData(nyukinkubunCode, nyukinkubunName, kingaku, bikou, row) {
      return {
        nyukinkubunCode: nyukinkubunCode,
        nyukinkubunName: nyukinkubunName,
        kingaku: kingaku,
        bikou: bikou,
        row: row
      };
    },
    addMeisai: function addMeisai(nyukinkubunCode, nyukinkubunName, kingaku, bikou, row) {
      var data = this.createMeisaiData(nyukinkubunCode, nyukinkubunName, kingaku, bikou, row);
      this.data.nyukinModel.meisai.push(data);
    },
    createUriageData: function createUriageData(keshikomiFlag, reception_number, syukkaDate, kingaku, row) {
      return {
        keshikomiFlag: keshikomiFlag,
        reception_number: reception_number,
        syukkaDate: syukkaDate,
        kingaku: kingaku,
        row: row
      };
    },
    addUriage: function addUriage(keshikomiFlag, reception_number, syukkaDate, kingaku, row) {
      var data = this.createUriageData(keshikomiFlag, reception_number, syukkaDate, kingaku, row);
      this.data.nyukinModel.uriage.push(data);
    },
    createSearchResultData: function createSearchResultData(reception_number, date, shijo, kingaku, bikou) {
      return {
        reception_number: reception_number,
        date: date,
        shijo: shijo,
        kingaku: kingaku,
        bikou: bikou
      };
    },
    addSearchResult: function addSearchResult(reception_number, date, shijo, kingaku, bikou) {
      var data = this.createSearchResultData(reception_number, date, shijo, kingaku, bikou);
      this.data.search.result(data);
    },
    nyukinConfirm: function nyukinConfirm() {
      var _this = this;

      this.$refs['nyukinForm'].validate(function (valid) {
        if (valid) {
          var select_value = _this.data.syoriKubun;

          if (select_value == 0) {
            //新規の時
            //更新ボタンの中身編集
            _this.data.updateButton = "新規";

            _this.addMeisai("", "", 0, "", _this.data.nyukinModel.meisai.length); //※axiosから取ってくる


            _this.addUriage(false, "10000", "2020-08-10", 0, _this.data.nyukinModel.uriage.length);
          } else {
            if (_this.data.receptionNumber == "") {
              alert("受付番号を検索してください");
              return false;
            } //※ここでaxiosで荷受け番号をもとにT荷受けからデータを取ってくる
            //検索で選択したデータを明細に追加する。とりあえずは適当なデータを明細に入れるので、後にdbから取ってくる
            //明細の行を初期化


            _this.data.nyukinModel.meisai = [];
            _this.data.nyukinModel.uriage = [];

            for (var i = 0; i < 4; i++) {
              var sample_meisai = _this.createMeisaiData("", "", i * 100, "", _this.data.nyukinModel.meisai.length);

              _this.data.nyukinModel.meisai.push(sample_meisai); //※axiosから取ってくる


              _this.addUriage(false, "10000", "2020-08-10", i * 100, _this.data.nyukinModel.uriage.length);
            }

            _this.addMeisai("", "", 0, "", _this.data.nyukinModel.meisai.length);

            if (select_value == 2) {
              //更新ボタンの中身編集
              _this.data.updateButton = "削除";
            }
          }

          _this.data.disabled.nyukinConfirm = true;
          console.log(_this.data.nyukinModel.uriage);
        } else {
          return false;
        }
      });
    },
    meisaiRemove: function meisaiRemove(index) {
      if (this.data.nyukinModel.meisai.length > 1) {
        //明細の削除
        this.data.nyukinModel.meisai.splice(index, 1);
      }
    },
    meisaiReturn: function meisaiReturn(row, index) {
      if (this.data.syoriKubun == 0) {
        var meisai = this.data.nyukinModel.meisai[index];
        console.log(row, meisai);
        meisai.nyukinkubunCode = "";
        meisai.nyukinkubunName = "";
        meisai.kingaku = "";
        meisai.bikou = "";
      } else if (this.data.syoriKubun == 1) {//※処理区分が1の時axiosでとってきて戻す処理
      }
    },
    changeMeisaiData: function changeMeisaiData(index) {
      console.log(index);

      if (index == this.data.nyukinModel.meisai.length - 1) {
        this.addMeisai("", "", 0, "", this.data.nyukinModel.meisai.length);
      }
    },
    nyukinSearch: function nyukinSearch() {
      //検索を更新するときはresultを初期化
      this.data.search.result = []; //※ここでaxiosで検索データを取ってくる
      //とりあえずのデータ、サーバーとつないだらここはajaxx

      var sample_data = this.createSearchResultData("101010", "2020-10-10", 2, 1000, "あああああ");

      for (var i = 0; i < 4; i++) {
        this.data.search.result.push(sample_data);
      }
    },
    getShijoName: function getShijoName(code) {
      var result = ""; //※ここでaxiosでデータを取ってくる

      for (var i = 0; i < this.data.shijoData.length; i++) {
        if (this.data.shijoData[i].shijoCD == code) {
          result = this.data.shijoData[i].shijomei;
        }
      }

      return result;
    },
    //クリックした検索結果を青くする
    searchSelectedTr: function searchSelectedTr(index) {
      this.data.search.selectedIndex = index;
    },
    searchResultSelect: function searchResultSelect(search_result) {
      this.data.receptionNumber = search_result.reception_number; //モーダルを閉じる

      this.data.dialogVisible = false;
    },
    init: function init() {
      //初期化
      this.data.nyukinModel.meisai = [];
      this.data.nyukinModel.uriage = [];
      this.data.search.result = [];
      this.data.nyukinModel.nyukinDate = "";
      this.data.nyukinModel.shijoCode = "";
      this.data.nyukinModel.shijoName = "";
      this.data.receptionNumber = "";
      this.data.syoriKubun = 0;
      this.data.disabled.nyukinConfirm = false;
    },
    update: function update() {
      var message;

      if (this.data.syoriKubun == 0) {
        message = "登録しますか？";
      } else if (this.data.syoriKubun == 1) {
        message = "更新しますか？";
      } else if (this.data.syoriKubun == 2) {
        message = "削除しますか？";
      }

      if (this.data.syoriKubun == 0 || this.data.syoriKubun == 1) {
        this.$refs['nyukinForm'].validate(function (valid) {
          if (valid) {
            if (window.confirm(message)) {
              alert("実行");
            }
          } else {
            return false;
          }
        });
      } else {
        if (window.confirm(message)) {
          alert("実行");
        }
      }
    },
    finish: function finish() {
      alert("終了");
    }
  },
  created: function created() {
    //※axiosでとってくる
    this.data.shijoData = [{
      shijoCD: "0",
      shijomei: "高岡"
    }, {
      shijoCD: "1",
      shijomei: "富山"
    }, {
      shijoCD: "2",
      shijomei: "丸果"
    }, {
      shijoCD: "3",
      shijomei: "福井"
    }];
    this.data.nyukinModel.nyukinDate = time.nowJapanDateInput();
  },
  mounted: function mounted() {},
  watch: {}
};
