import KeyboardEvents from '@/components/KeyboardEvents.vue';
import config from '@/config.js';
import validation from '@/js/status/validation.js';
import ElIncrementSearch from '@/components/IncrementSearch.vue';
import time from '@/js/status/time.js';
import url from '@/js/api_url.js';
export default {
  name: 'niukeNyuryoku',
  components: {
    KeyboardEvents: KeyboardEvents,
    ElIncrementSearch: ElIncrementSearch
  },
  data: function data() {
    return {
      state1: "",
      //methodsで使う際のnodeをmountedで登録し、取得する手間を省く
      element: {
        niukeDate: null,
        syoriKubun: null,
        receptionNumber: null,
        receptionNumberSearch: null,
        syouhinName: null,
        hinsyuCode: null,
        updateButton: null
      },
      data: {
        pageNumber: 1,
        numberOfTerminal: null,
        search: {
          dateStart: "",
          dateEnd: "",
          seisansya: {
            code: "",
            name: ""
          },
          result: [],
          selectedIndex: null
        },
        first: {
          model: {
            syoriKubun: 1,
            niukeDate: "",
            kaisu: 0,
            syouhinName: "",
            syouhinCode: "",
            jikayouChecked: false,
            niukeHyouChecked: true,
            niukeHyouHikaeChecked: true
          },
          validation: {
            syoriKubun: [{
              required: true,
              message: '処理区分が入力されていません',
              trigger: 'blur'
            }],
            niukeDate: [{
              required: true,
              message: '荷受日付が入力されていません',
              trigger: 'blur'
            }],
            kaisu: [{
              required: true,
              message: '回数が入力されていません',
              trigger: 'blur'
            }],
            syouhinCode: [{
              required: true,
              message: '商品コードが入力されていません',
              trigger: 'change'
            }]
          }
        },
        second: {
          model: {
            niukeDate: "",
            seisansyaCode: "",
            seisansyaName: "",
            hinsyuCode: "",
            meisai: []
          }
        },
        //更新ボタンの文字
        updateButton: "更新",
        disabled: {
          niukeConfirm: false
        },
        shizaidai: 0,
        zentokin: 0,
        dialogVisible: false,
        total: {
          number_of_box: 0,
          bara: 0,
          number: 0,
          kayoibako: 0
        },
        //処理区分、第一画面の処理区分はfirst、第二画面はsecond
        syoriKubun: {
          first: "0",
          second: "0"
        },
        syouhin: {
          first: {
            name: "",
            code: ""
          },
          second: {
            code: "",
            name: "",
            niuke_unit: "",
            //キーをサイズコードにして、サイズ毎の入数を呼び出す
            number_per_box: {}
          }
        },
        receptionNumber: "",
        //荷受日付、フォーマットはinputのdateに格納できるstringの形で、
        niukeDate: {
          first: '',
          second: ""
        },
        seisansya: {
          code: "",
          name: ""
        },
        note: {},
        reason_of_return: {},
        size: {},
        tokyu: {},
        hinsyu: []
      }
    };
  },
  methods: {
    keyboardEvent: function keyboardEvent(e) {
      if (e.keyCode == config.KEY_CODES.F5) {
        if (this.data.pageNumber == 1) {
          this.searchCheck();
        }
      }

      if (e.keyCode == config.KEY_CODES.F9) {
        if (this.data.pageNumber == 2) {
          this.update();
        }
      }

      if (e.keyCode == config.KEY_CODES.F10) {
        if (this.data.pageNumber == 2) {
          this.init();
        }
      }

      if (e.keyCode == config.KEY_CODES.F12) {
        this.finish();
      }
    },
    searchCheck: function searchCheck() {
      var _this = this;

      this.$refs['firstForm'].validate(function (valid) {
        if (valid) {
          _this.data.pageNumber = 2;
        }
      });
    },
    createMeisaiData: function createMeisaiData(code, size, tokyu, number_of_box, bara, number, note, reason_of_return) {
      return {
        code: code,
        size: size,
        tokyu: tokyu,
        number_of_box: number_of_box,
        bara: bara,
        number: number,
        note: note,
        reason_of_return: reason_of_return
      };
    },
    addMeisai: function addMeisai(code, size, tokyu, number_of_box, bara, number, note, reason_of_return) {
      var data = this.createMeisaiData(code, size, tokyu, number_of_box, bara, number, note, reason_of_return);
      this.data.second.model.meisai.push(data);
    },
    createSearchResultData: function createSearchResultData(date, reception_number, seisansya, syouhin, hinsyu, frequency, total, shizai, zentokin) {
      return {
        date: date,
        reception_number: reception_number,
        seisansya: seisansya,
        syouhin: syouhin,
        hinsyu: hinsyu,
        frequency: frequency,
        total: total,
        shizai: shizai,
        zentokin: zentokin
      };
    },
    addSearchResult: function addSearchResult(date, reception_number, seisansya, syouhin, hinsyu, frequency, total, shizai, zentokin) {
      var data = this.createSearchResultData(date, reception_number, seisansya, syouhin, hinsyu, frequency, total, shizai, zentokin);
      this.data.search.result.push(data);
    },
    niukeConfirm: function niukeConfirm() {
      var _this2 = this;

      //仕様書を見た感じ提出前にチェックする必要性が無いように感じるが、もし入力チェックがある場合はここに追加
      var select_value = this.data.syoriKubun.second;

      if (select_value == 0) {
        //新規の時
        //第一画面の荷受け日付を第二画面の荷受け日付に変える
        this.data.second.model.niukeDate = this.data.first.model.niukeDate; //第一画面の商品区分を元に商品名プロパティに入れる。そのデータを取ってくる。とりあえず定数を入れておくので変えてください。

        this.data.syouhin.second.code = this.data.first.model.syouhinCode;
        this.data.syouhin.second.name = this.data.first.model.syouhinName;
        this.getHinsyuSelect(); //更新ボタンの中身編集

        this.data.updateButton = "新規";
        this.getMasterData(true);
      } else if (select_value == 1 || select_value == 2) {
        if (this.data.receptionNumber == "") {
          alert("受付番号を検索してください");
          return false;
        } //※ここでaxiosで荷受け番号をもとにT荷受けからデータを取ってくる


        axios.get(url.niukeNyuryoku + "?reception_number=" + this.data.receptionNumber).then(function (response) {
          console.log(response.data);
          var data = response.data[0];

          if (data.meisaiData.length == 0) {
            alert("データがありません");
            return false;
          } //検索で選択したデータを明細に追加する。とりあえずは適当なデータを明細に入れるので、後にdbから取ってくる
          //明細の行を初期化


          _this2.data.second.model.meisai = [];
          _this2.data.second.model.niukeDate = data.date;
          _this2.data.second.model.seisansyaCode = String(data.seisansyacd);
          _this2.data.second.model.seisansyaName = data.seisansya;
          _this2.data.syouhin.second.name = data.syohin;
          _this2.data.syouhin.second.code = data.syohincd;
          _this2.data.total.kayoibako = data.total;
          _this2.data.shizaidai = data.shizai;
          _this2.data.zentokin = data.zentokin;
          console.log("商品コード:", _this2.data.syouhin.second.code);

          _this2.getHinsyuSelect();

          _this2.data.second.model.hinsyuCode = data.hinsyucd;
          console.log(_this2.data.second.model.hinsyuCode);

          if (select_value == 2) {
            //更新ボタンの中身編集
            _this2.data.updateButton = "削除";
          } else if (select_value == 1) {
            _this2.data.updateButton = "編集";
          }

          _this2.getMasterData(false, data.meisaiData);
        }).catch(function (response) {
          return console.log(response);
        });
      }
    },
    meisaiRemove: function meisaiRemove(index) {
      if (this.data.second.model.meisai.length > 1) {
        //明細の削除
        this.data.second.model.meisai.splice(index, 1);
      }
    },
    //明細のコードの入力チェック
    meisaiCodeCheck: function meisaiCodeCheck(meisai_data, input, index) {
      var input_code = input.value; //数値バリデーション

      if (!validation.validation.is_intager_filling_zero(input_code)) {
        alert(validation.error_message.is_intager_filling_zero("コード"));
        input.value = meisai_data.code;
        return false;
      }

      if (input_code.length != 4) {
        alert("文字数は4桁である必要があります。");
        input.value = meisai_data.code;
        return false;
      }

      var size_code = input_code.substr(0, 2);
      var tokyu_code = input_code.substr(2, 2);
      var size_name = this.sizeSearch(size_code);
      var tokyu_name = this.tokyuSearch(tokyu_code);

      if (size_name == undefined) {
        alert("サイズがありません");
        input.value = meisai_data.code;
        return false;
      }

      if (tokyu_name == undefined) {
        alert("等級がありません");
        input.value = meisai_data.code;
        return false;
      }

      meisai_data.size = size_code;
      meisai_data.tokyu = tokyu_code;
      meisai_data.code = input.value;
      this.numberChange(meisai_data);
    },
    sizeSearch: function sizeSearch(size) {
      //検索して、ものがあれば名称を返し、無ければundefinedで返す
      var result;
      var is_exist = false;
      var key;

      for (var i in this.data.size) {
        if (i == size) {
          is_exist = true;
          key = i;
        }
      }

      if (is_exist) {
        result = this.data.size[key];
      }

      return result;
    },
    tokyuSearch: function tokyuSearch(tokyu) {
      //検索して、ものがあれば名称を返し、無ければundefinedで返す
      var result;
      var is_exist = false;
      var key;

      for (var i in this.data.tokyu) {
        if (i == tokyu) {
          is_exist = true;
          key = i;
        }
      }

      if (is_exist) {
        result = this.data.tokyu[key];
      }

      return result;
    },
    //箱数またはバラの値を変化させた時の処理,paramは箱数かバラかのパラメーター
    numberChange: function numberChange(meisai_data) {
      if (this.data.syouhin.second.niuke_unit == 0) {
        if (meisai_data.number_of_box == undefined || meisai_data.size == null || meisai_data.bara == undefined) {
          meisai_data.number = 0;
          return false;
        }

        meisai_data.number = Number(this.data.syouhin.second.number_per_box[meisai_data.size] * meisai_data.number_of_box) + Number(meisai_data.bara);
      } else if (this.syouhin.second.niuke_unit == 1) {
        if (meisai_data.number_of_box == undefined) {
          meisai_data.number = 0;
          return false;
        }

        meisai_data.number = meisai_data.number_of_box;
      }

      var total_of_box = this.totalCount("number_of_box");
      var total_of_bara = this.totalCount("bara");
      var total_number = this.totalCount("number");
      this.data.total.number_of_box = total_of_box;
      this.data.total.bara = total_of_bara;
      this.data.total.number = total_number;
    },
    totalCount: function totalCount(param) {
      var result = 0;

      for (var i = 0; i < this.data.second.model.meisai.length; i++) {
        var target_number = this.data.second.model.meisai[i][param];
        result += target_number;
      }

      return result;
    },
    niukeSearch: function niukeSearch() {
      var _this3 = this;

      if (this.data.search.dateStart == "" || this.data.search.dateEnd == "") {
        alert("日付が入力されていません");
        return false;
      }

      if (new Date(this.data.search.dateStart).getTime() > new Date(this.data.search.dateEnd).getTime()) {
        alert("日付の関係が逆になっています");
        return false;
      } //検索を更新するときはresultを初期化


      this.data.search.result = [];
      var postUrl = url.niukeNyuryokuKensaku + "?dateKagen=" + this.data.search.dateStart + "&dateJyogen=" + this.data.search.dateEnd + "&Syorikbn=" + this.data.first.model.syoriKubun;

      if (this.data.search.seisansya.code != "") {
        postUrl += "&seisansya=" + this.data.search.seisansya.code;
      } else {
        postUrl += "&seisansya=null";
      }

      axios.get(postUrl).then(function (response) {
        console.log(response.data);

        for (var i = 0; i < response.data.length; i++) {
          var data = response.data[i];

          _this3.addSearchResult(data.date, data.reception_number, data.seisansya, data.syouhin, data.hinsyu, data.frequency, data.total, data.shizai, data.zentokin);
        }
      }).catch(function (response) {
        return console.log(response);
      });
    },
    //クリックした検索結果を青くする
    searchSelectedTr: function searchSelectedTr(index) {
      this.data.search.selectedIndex = index;
    },
    searchResultSelect: function searchResultSelect(search_result) {
      this.data.receptionNumber = search_result.reception_number; //モーダルを閉じる

      this.data.dialogVisible = false;
    },
    getSyouhinData: function getSyouhinData() {//※ここで商品コードを元に商品データを取ってきてdata.syouhin.secondの中に入れる
    },
    init: function init() {
      //初期化
      this.data.second.model.meisai = [];
      this.data.search.result = [];
      this.data.hinsyu = [];
      this.data.second.model.niukeDate = "";
      this.data.second.model.seisansyaCode = "";
      this.data.second.model.seisansyaName = "";
      this.data.syouhin.second.name = "";
      this.data.second.model.hinsyuCode = "";
      this.data.second.model.hinsyuName = "";
      this.data.receptionNumber = "";
      this.data.syoriKubun.second = "0";
      this.data.total.kayoibako = 0;
      this.data.disabled.niukeConfirm = false;
      this.data.total.number_of_box = 0;
      this.data.total.bara = 0;
      this.data.total.number = 0;
      this.data.shizaidai = 0;
      this.data.zentokin = 0;
    },
    update: function update() {
      var _this4 = this;

      this.$refs['secondForm'].validate(function (valid) {
        if (valid) {
          if (_this4.data.syoriKubun.second == 0 && _this4.data.first.model.syoriKubun == 1) {
            if (_this4.data.second.model.meisai.length == 0) {
              alert("追加する行がありません");
              return false;
            }
          }

          var message;

          if (_this4.data.syoriKubun.second == 0) {
            message = "登録しますか？";
          } else if (_this4.data.syoriKubun.second == 1) {
            message = "更新しますか？";
          } else if (_this4.data.syoriKubun.second == 2) {
            message = "削除しますか？";
          }

          if (window.confirm(message)) {
            var postArray = {
              "Reception_number": 0,
              "Seisansyacd": Number(_this4.data.second.model.seisansyaCode),
              "SyoriKbn": _this4.data.first.model.syoriKubun,
              "Date": _this4.data.second.model.niukeDate,
              "Kaisu": _this4.$store.state.login.kaisu,
              "Syohin": Number(_this4.data.syouhin.second.code),
              "Hinsyu": _this4.data.second.model.hinsyuCode,
              "Nyuryokutantocd": _this4.$store.state.login.userCode,
              "Hakogoukei": _this4.data.total.kayoibako,
              "ShizaiDai": _this4.data.shizaidai,
              "MaewatasiKin": _this4.data.zentokin,
              "Nendo": Number(_this4.data.second.model.niukeDate.split("-")[0]),
              "JikayoFlg": 0,
              "Data": []
            };

            if (_this4.data.first.model.jikayouChecked) {
              postArray.JikayoFlg = 1;
            }

            if (_this4.data.first.model.syoriKubun == 9) {
              postArray.Hinsyu = 0;
            }

            if (_this4.data.receptionNumber != "") {
              postArray.Reception_number = Number(_this4.data.receptionNumber);
            }

            for (var i = 0; i < _this4.data.second.model.meisai.length; i++) {
              var data = _this4.data.second.model.meisai[i];

              if (data.number == "") {
                data.number = 0;
              }

              postArray.Data.push({
                "Row": i,
                "Size": data.size,
                "Tokyu": data.tokyu,
                "Hako": data.number_of_box,
                "Bara": data.bara,
                "Suryo": data.number,
                "BikouCD": data.note,
                "HenpinRiyu": data.reason_of_return
              });
            }

            console.log(postArray);

            if (_this4.data.syoriKubun.second == 0) {
              axios.post(url.niukeNyuryoku, postArray).then(function (response) {
                _this4.$message({
                  message: '登録に成功しました',
                  type: 'success'
                });

                if (_this4.data.first.model.syoriKubun == 1) {
                  window.open(response.data.url);
                }

                _this4.init();
              }).catch(function (response) {
                _this4.$message({
                  message: '通信に失敗しました',
                  type: 'error'
                });

                console.log(response);
              });
            } else if (_this4.data.syoriKubun.second == 1) {
              axios.put(url.niukeNyuryoku, postArray).then(function (response) {
                _this4.$message({
                  message: '修正に成功しました',
                  type: 'success'
                });

                if (_this4.data.first.model.syoriKubun == 1) {
                  if (window.confirm("再印刷しますか？")) {
                    window.open(response.data.url);
                  }
                }

                _this4.init();
              }).catch(function (response) {
                _this4.$message({
                  message: '通信に失敗しました',
                  type: 'error'
                });

                console.log(response);
              });
            } else if (_this4.data.syoriKubun.second == 2) {
              postArray = {
                "Reception_number": _this4.data.receptionNumber
              };
              axios.delete(url.niukeNyuryoku, {
                params: postArray
              }).then(function (response) {
                _this4.$message({
                  message: '削除に成功しました',
                  type: 'success'
                });

                _this4.init();
              }).catch(function (response) {
                _this4.$message({
                  message: '通信に失敗しました',
                  type: 'error'
                });

                console.log(response);
              });
            }
          }
        }
      });
    },
    finish: function finish() {
      this.$router.push('/hoshigaki/');
    },
    getHinsyuSelect: function getHinsyuSelect() {
      var _this5 = this;

      var getUrl = url.createGetUrl(url.syohinHinsyuTokyuPatternMaster, [{
        key: "paramSyohin",
