import Card from '@/components/Card/Card.vue';
import KeyboardEvents from '@/components/KeyboardEvents.vue';
import config from '@/config.js';
import exsist from '@/js/status/exsist.js';
import time from '@/js/status/time.js';
import ElIncrementSearch from '@/components/IncrementSearch.vue';
import url from '@/js/api_url.js';
export default {
  name: 'niukeNyuryokuSyoki',
  components: {
    Card: Card,
    KeyboardEvents: KeyboardEvents,
    ElIncrementSearch: ElIncrementSearch
  },
  data: function data() {
    return {
      //端末番号持ってくる
      //methodsで使う際のnodeをmountedで登録し、取得する手間を省く
      element: {
        syoriKubun: null,
        niukeNyuryokuForm: null
      },
      data: {
        pageNumber: 1,
        first: {
          model: {
            syukkaDate: "",
            syukkaDenpyouChecked: false,
            syukkaKeisansyoChecked: false,
            nouninsyoChecked: false
          },
          validation: {
            syukkaDate: [{
              required: true,
              message: '出荷日付が入力されていません',
              trigger: 'blur'
            }]
          }
        },
        syukkaDate: {
          first: "",
          second: ""
        },
        checked: {
          goukeiInji: false
        },
        disabled: {
          updateButton: true,
          meisaiIrisu: false,
          syukkaConfirm: false
        },
        second: {
          model: {
            syukkaDate: "",
            shijyoCode: "",
            shijyoName: "",
            bumonCode: "",
            bumonName: "",
            tesuryou: "",
            meisai: []
          }
        },
        selectedBunkatsuTankaIndex: null,
        billNumber: "",
        syoriKubun: 0,
        dialogVisible: false,
        search: {
          dateStart: "0001-01-01",
          dateEnd: "9999-12-31",
          shijoCode: "",
          shijoName: "",
          result: [],
          selectedIndex: null
        },
        shijo: {
          code: "",
          name: ""
        },
        bumon: {
          code: "",
          name: ""
        },
        tesuryou: 0,
        meisai: [],
        updateButton: "登録",
        syouhin: {},
        hinsyu: [],
        size: [],
        housou: [],
        shijoTokyu: [],
        kumiaiTokyu: [],
        unchin: [],
        shijyo: [],
        bunkatsuTankaNyuryoku: {
          index: null,
          syouhin: "",
          hinsyu: "",
          size: "",
          housou: "",
          tokyu: "",
          totalMaisu: "",
          meisai: [{
            maisu: 0,
            tanka: 0
          }]
        }
      }
    };
  },
  methods: {
    keyboardEvent: function keyboardEvent(e) {
      if (e.keyCode == config.KEY_CODES.F5) {
        if (this.data.pageNumber == 1) {
          this.confirm();
        }
      }

      if (e.keyCode == config.KEY_CODES.F9) {
        if (this.data.pageNumber == 2) {
          if (this.data.disabled.syukkaConfirm) {
            this.update();
          }
        }

        if (this.data.pageNumber == 3) {
          this.bunkatsuTankaNyuryokuUpdate();
        }
      }

      if (e.keyCode == config.KEY_CODES.F10) {
        if (this.data.pageNumber == 2) {
          this.init();
        }
      }

      if (e.keyCode == config.KEY_CODES.F12) {
        this.finish();
      }
    },
    confirm: function confirm() {
      var _this = this;

      this.$refs['firstForm'].validate(function (valid) {
        if (valid) {
          _this.data.pageNumber = 2;
        }
      });
    },
    syukkaConfirm: function syukkaConfirm() {
      var _this2 = this;

      //仕様書を見た感じ提出前にチェックする必要性が無いように感じるが、もし入力チェックがある場合はここに追加
      var select_value = this.data.syoriKubun;

      if (select_value == 0) {
        //新規の時
        //第一画面の荷受け日付を第二画面の荷受け日付に変えて、disabledにする
        this.data.second.model.syukkaDate = this.data.first.model.syukkaDate;
        this.addMeisai('', null, '', '', '', 0, 1, '', '', 0, 0, 0, 0, 0, '', '', []);
      } else if (select_value == 1 || select_value == 2) {
        if (this.data.billNumber == "") {
          alert("伝票番号を検索してください");
          return false;
        }

        var getUrl = url.createGetUrl(url.syukkaNyuryoku, [{
          key: "paramSyukkaBango",
          value: this.data.billNumber
        }]);
        axios.get(getUrl).then(function (response) {
          console.log(response.data);
          var data = response.data[0];

          if (data.meisaiData.length == 0) {
            alert("データがありません");
            return false;
          } //※検索で選択したデータを明細に追加する。とりあえずは適当なデータを明細に入れるので、後にdbから取ってくる
          //明細の行を初期化


          _this2.data.second.model.meisai = [];
          _this2.data.second.model.syukkaDate = data.syukkaDate;
          _this2.data.second.model.shijyoCode = data.shijouCode;
          _this2.data.second.model.shijyoName = data.shijouName;
          _this2.data.second.model.tesuryou = data.tesuryou;
          _this2.data.second.model.bumonCode = data.bumonCode;
          _this2.data.second.model.bumonName = data.bumonName;

          for (var i = 0; i < data.meisaiData.length; i++) {
            var meisai = data.meisaiData[i];

            _this2.addMeisai(meisai.code, meisai.syouhin, meisai.hinsyu, meisai.size, meisai.housou, meisai.sainyuryokukbn, meisai.irisu, meisai.shijotokyu, meisai.kumiaitokyu, meisai.kouirisu, meisai.kousu, meisai.maisu, meisai.tanka, meisai.price, meisai.unchin, meisai.unchincd, meisai.meisaibunkatuData);
          }

          if (select_value == 2) {
            //更新ボタンの中身編集
            _this2.data.updateButton = "削除";
          } else if (select_value == 1) {
            _this2.data.updateButton = "編集";
          }
        }).catch(function (response) {
          return console.log(response);
        });
      }

      this.data.disabled.syukkaConfirm = true;
    },
    createMeisaiData: function createMeisaiData(code, syouhin, hinsyu, size, housou, irisu, sainyuryokukbn, sijotokyu, kumiaitokyu, kouirisu, kousu, maisu, tanka, price, unchin, unchincd, meisaibunkatuData) {
      return {
        code: code,
        syouhin: syouhin,
        hinsyu: hinsyu,
        size: size,
        housou: housou,
        irisu: irisu,
        sainyuryokukbn: sainyuryokukbn,
        sijotokyu: sijotokyu,
        kumiaitokyu: kumiaitokyu,
        kouirisu: kouirisu,
        kousu: kousu,
        maisu: maisu,
        tanka: tanka,
        price: price,
        unchin: unchin,
        unchincd: unchincd,
        meisaibunkatuData: meisaibunkatuData
      };
    },
    addMeisai: function addMeisai(code, syouhin, hinsyu, size, housou, irisu, sainyuryokukbn, sijotokyu, kumiaitokyu, kouirisu, kousu, maisu, tanka, price, unchin, unchincd, meisaibunkatuData) {
      var data = this.createMeisaiData(code, syouhin, hinsyu, size, housou, irisu, sainyuryokukbn, sijotokyu, kumiaitokyu, kouirisu, kousu, maisu, tanka, price, unchin, unchincd, meisaibunkatuData);
      this.data.second.model.meisai.push(data);
    },
    //クリックした検索結果を青くする
    searchSelectedTr: function searchSelectedTr(index) {
      this.data.search.selectedIndex = index;
    },
    searchResultSelect: function searchResultSelect(search_result) {
      this.data.billNumber = search_result.bill_number; //モーダルを閉じる

      this.data.dialogVisible = false;
    },
    createSearchResultData: function createSearchResultData(date, bill_number, row, shijo, syouhin, hinsyu, size, housou, tokyu, hiki, maisu, tanka, price, unchin) {
      return {
        date: date,
        bill_number: bill_number,
        row: row,
        shijo: shijo,
        syouhin: syouhin,
        hinsyu: hinsyu,
        size: size,
        housou: housou,
        tokyu: tokyu,
        hiki: hiki,
        maisu: maisu,
        tanka: tanka,
        price: price,
        unchin: unchin
      };
    },
    addSearchResult: function addSearchResult(date, bill_number, row, shijo, syouhin, hinsyu, size, housou, tokyu, hiki, maisu, tanka, price, unchin) {
      var data = this.createSearchResultData(date, bill_number, row, shijo, syouhin, hinsyu, size, housou, tokyu, hiki, maisu, tanka, price, unchin);
      this.data.search.result.push(data);
    },
    syukkaSearch: function syukkaSearch() {
      var _this3 = this;

      if (this.data.search.dateStart == "" || this.data.search.dateEnd == "") {
        alert("日付が入力されていません");
        return false;
      }

      if (new Date(this.data.search.dateStart).getTime() > new Date(this.data.search.dateEnd).getTime()) {
        alert("日付の関係が逆になっています");
        return false;
      } //検索を更新するときはresultを初期化


      this.data.search.result = [];
      var getArray = [{
        key: "paramKaisiSyukkaHiduke",
        value: this.data.search.dateStart
      }, {
        key: "paramSyuryoShukkaHiduke",
        value: this.data.search.dateEnd
      }];

      if (this.data.search.shijoCode != "") {
        getArray.push({
          key: "paramSijoCd",
          value: this.data.search.shijoCode
        });
      } else {
        getArray.push({
          key: "paramSijoCd",
          value: null
        });
      }

      var getUrl = url.createGetUrl(url.syukkaNyuryokuKensaku, getArray);
      axios.get(getUrl).then(function (response) {
        console.log(response.data);

        for (var i = 0; i < response.data.length; i++) {
          var data = response.data[i];

          _this3.addSearchResult(data.date, data.bill_number, data.row, data.shijo, data.syouhin, data.hinsyu, data.size, data.housou, data.tokyu, data.hiki, data.maisu, data.tanka, data.price, data.unchin);
        }
      }).catch(function (response) {
        return console.log(response);
      });
    },
    meisaiRemove: function meisaiRemove(index) {
      if (this.data.second.model.meisai.length > 1) {
        //明細の削除
        this.data.second.model.meisai.splice(index, 1);
      }
    },
    //明細のコードの入力チェック
    meisaiCodeCheck: function meisaiCodeCheck(meisai_data, input, index) {
      var input_code = input.value;

      if (input_code.length != 5) {
        alert("文字数は5桁である必要があります。");
        input.value = meisai_data.code;
        return false;
      }

      var syouhin_code = Number(input_code.substr(0, 1));
      var hinsyu_code = Number(input_code.substr(1, 1));
      var size_code = Number(input_code.substr(2, 2));
      var housou_code = input_code.substr(4, 1);
      var syouhin_name = this.syouhinSearch(syouhin_code);
      var hinsyu = this.hinsyuSearch(hinsyu_code, syouhin_code);
      var size = this.sizeSearch(size_code, syouhin_code);
      var housou = this.housouSearch(housou_code, syouhin_code);
      var alert_name;

      if (syouhin_name == undefined) {
        alert_name = "商品";
      }

      if (hinsyu == undefined) {
        alert_name = "品種";
      }

      if (size == undefined) {
        alert_name = "サイズ";
      }

      if (housou == undefined) {
        alert_name = "包装";
      }

      if (alert_name != undefined) {
        alert(alert_name + "がありません");
        input.value = meisai_data.code;
        return false;
      } //入数と入数再入力区分、運賃コードの取得


      meisai_data.sainyuryokukbn = housou.sainyuryokukbn;
      meisai_data.irisu = housou.datas[size_code];
      meisai_data.unchincd = housou.unchincd;
      this.calculateUnchin(meisai_data);
      meisai_data.syouhin = syouhin_code;
      meisai_data.hinsyu = hinsyu_code;
      meisai_data.size = size_code;
      meisai_data.housou = housou_code;
      meisai_data.code = input.value;
      console.log(this.data.hinsyu);
    },
    getHinsyuArrrayBySyouhinCode: function getHinsyuArrrayBySyouhinCode(syouhinCode) {
      var result = [];

      for (var i = 0; i < this.data.hinsyu.length; i++) {
        if (this.data.hinsyu[i].syohincd == syouhinCode) {
          result.push(this.data.hinsyu[i]);
        }
      }

      return result;
    },
    getSizeArrrayBySyouhinCode: function getSizeArrrayBySyouhinCode(syouhinCode) {
      var result = [];

      for (var i = 0; i < this.data.size.length; i++) {
        if (this.data.size[i].syohincd == syouhinCode) {
          result.push(this.data.size[i]);
        }
      }

      return result;
    },
    getHousouArrrayBySyouhinCode: function getHousouArrrayBySyouhinCode(syouhinCode) {
      var result = [];

      for (var i = 0; i < this.data.housou.length; i++) {
        if (this.data.housou[i].syohincd == syouhinCode) {
          result.push(this.data.housou[i]);
        }
      }

      return result;
    },
    //とりあえずdataから検索してます。検索をリアルタイムで行う場合は、ここにajax式を入れてdataの該当するプロパティを変えます
    syouhinSearch: function syouhinSearch(syouhin) {
      return exsist.is_exsist_by_key(syouhin, this.data.syouhin);
    },
    hinsyuSearch: function hinsyuSearch(hinsyu, syouhin) {
      return exsist.is_exsist_by_object_value(hinsyu, this.getHinsyuArrrayBySyouhinCode(syouhin), "hinsyucd");
    },
    sizeSearch: function sizeSearch(size, syouhin) {
      return exsist.is_exsist_by_object_value(size, this.getSizeArrrayBySyouhinCode(syouhin), "sizecd");
    },
    housouSearch: function housouSearch(housou, syouhin) {
      return exsist.is_exsist_by_object_value(housou, this.getHousouArrrayBySyouhinCode(syouhin), "hosocd");
    },
    getShijyoTokyuByShijyoTokyuCD: function getShijyoTokyuByShijyoTokyuCD(shijyoTokyuCD) {
      var result;

      for (var i = 0; i < this.data.shijoTokyu.length; i++) {
        if (this.data.shijoTokyu[i].shijyotokyu == shijyoTokyuCD) result = this.data.shijoTokyu[i];
      }

      return result;
    },
    getShijyoByShijyoCD: function getShijyoByShijyoCD(shijyoCD) {
      var result;

      for (var i = 0; i < this.data.shijyo.length; i++) {
        if (this.data.shijyo[i].shijyoCD == shijyoCD) result = this.data.shijyo[i];
      }

      return result;
    },
    getUnchin: function getUnchin(unchinCD, unchinRank) {
      var unchin;

      for (var i = 0; i < this.data.unchin.length; i++) {
        var data = this.data.unchin[i];

        if (data.unchinCD == unchinCD && data.unchinRank == unchinRank) {
          unchin = data.unchin;
        }
      }

      return unchin;
    },
    calculateUnchin: function calculateUnchin(meisai) {
      if (meisai.unchincd == "" || meisai.maisu == undefined || this.data.second.model.shijyoCode == undefined || this.data.second.model.shijyoCode == "") {
        meisai.unchin = "";
      } else {
        var shijyo = this.getShijyoByShijyoCD(Number(this.data.second.model.shijyoCode));
        var unchin = this.getUnchin(meisai.unchincd, shijyo.unchinRank);
        meisai.unchin = unchin * meisai.maisu;
      }
    },
    changeShijyoCD: function changeShijyoCD(new_value) {
      this.data.second.model.shijyoName = new_value.shijyomei;

      if (new_value.tesuryoKbn == 1) {
        this.data.second.model.tesuryou = 0;
      } else {
        this.data.second.model.tesuryou = new_value.tesuryoRate;
      }

      for (var i = 0; i < this.data.second.model.meisai.length; i++) {
        this.calculateUnchin(this.data.second.model.meisai[i]);
      }
    },
    changeMeisaiMaisu: function changeMeisaiMaisu(meisai_data) {
      console.log(meisai_data);

      if (meisai_data.kouirisu == undefined || meisai_data.kousu == undefined) {
        meisai_data.maisu = "";
      } else {
        meisai_data.maisu = meisai_data.kouirisu * meisai_data.kousu;
      }

      this.calculateUnchin(meisai_data);
    },
    changeMeisaiPrice: function changeMeisaiPrice(meisai_data) {
      if (meisai_data.maisu == undefined || meisai_data.tanka == undefined) {
        meisai_data.price = "";
      } else {
